import React from "react";
import "./IssueList.css";
import { updateIssueStatus } from "../services/issueService";

const IssueList = ({ issues, refresh }) => {
  const extractImages = (body) => {
    if (!body) return [];
    const regex = /!\[.*?\]\((.*?)\)/g;
    const matches = [...body.matchAll(regex)];
    return matches.map((m) => m[1]);
  };

  const handleMark = async (id, status) => {
    await updateIssueStatus(id, status);
    refresh();
  };

  const truncateText = (text, length = 150) => {
    if (!text) return "";
    return text.length > length ? text.slice(0, length) + "..." : text;
  };

  return (
    <div>
      <table>
        <thead>
          <tr>
            <th>Title</th>
            <th>Repository</th>
            <th>Reporter</th>
            <th>Body</th> {/* New column */}
            <th>Link</th>
            <th>Screenshots</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {issues.map((issue) => {
            const imageUrls = extractImages(issue.body);

            return (
              <tr key={issue._id}>
                <td>{issue.title}</td>
                <td>{issue.repo}</td>
                <td>{issue.reporter}</td>
                <td title={issue.body}>{truncateText(issue.body)}</td> {/* Shows full on hover */}
                <td>
                  <a href={issue.url} target="_blank" rel="noopener noreferrer">
                    View on GitHub
                  </a>
                </td>
                <td>
                  {imageUrls.length > 0 ? (
                    imageUrls.map((url, index) => (
                      <img
                        key={index}
                        src={url}
                        alt={`screenshot-${index}`}
                        width="60"
                        style={{ cursor: "pointer", marginRight: "5px", borderRadius: "4px" }}
                        onClick={() => window.open(url, "_blank")}
                      />
                    ))
                  ) : (
                    "No Image"
                  )}
                </td>
                <td>
                  {(!issue.status || issue.status === "unreviewed") ? (
                    <>
                      <button
                        onClick={() => handleMark(issue._id, "valid")}
                        style={{ marginRight: "8px", background: "green", color: "white" }}
                      >
                        Mark Valid
                      </button>
                      <button
                        onClick={() => handleMark(issue._id, "invalid")}
                        style={{ background: "red", color: "white" }}
                      >
                        Mark Invalid
                      </button>
                    </>
                  ) : (
                    <span style={{ fontWeight: "bold" }}>
                      âœ… {issue.status.toUpperCase()}
                    </span>
                  )}
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default IssueList;
