import React, { useState } from "react";
import { updateIssueStatus } from "../services/issueService";
import "./IssueList.css";

const IssueList = ({ issues, refresh }) => {
  const [modalContent, setModalContent] = useState("");
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Extract all image URLs from markdown body
  const extractImages = (body) => {
  if (!body) return [];

  const urls = [];

  // 1️⃣ Extract Markdown images ![alt](url)
  const mdRegex = /!\[.*?\]\((.*?)\)/g;
  const mdMatches = [...body.matchAll(mdRegex)];
  mdMatches.forEach((m) => urls.push(m[1]));

  // 2️⃣ Extract HTML <img src="..."> tags
  const htmlRegex = /<img[^>]+src=["'](.*?)["']/g;
  const htmlMatches = [...body.matchAll(htmlRegex)];
  htmlMatches.forEach((m) => urls.push(m[1]));

  return urls;
};


  // Extract text from body by removing all image markdown
  const extractTextOnly = (body) => {
    if (!body) return "";
    return body.replace(/!\[.*?\]\(.*?\)/g, "").trim();
  };

  const handleMark = async (id, status) => {
    await updateIssueStatus(id, status);
    refresh(); // refresh list after marking
  };

  const openModal = (content) => {
    setModalContent(content);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setModalContent("");
  };

  return (
    <div>
      <table>
        <thead>
          <tr>
            <th>Title</th>
            <th>Repository</th>
            <th>Reporter</th>
            <th>Body</th>
            <th>Link</th>
            <th>Screenshots</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {issues.map((issue) => {
            const imageUrls = extractImages(issue.body);
            const textOnly = extractTextOnly(issue.body);

            return (
              <tr key={issue._id}>
                <td>{issue.title}</td>
                <td>{issue.repo}</td>
                <td>{issue.reporter}</td>
                <td>
                  {textOnly ? (
                    <span
                      style={{ color: "blue", cursor: "pointer", textDecoration: "underline" }}
                      onClick={() => openModal(textOnly)}
                    >
                      View Description
                    </span>
                  ) : (
                    <span style={{ color: "#777" }}>—</span>
                  )}
                </td>
                <td>
                  <a href={issue.url} target="_blank" rel="noopener noreferrer">
                    View on GitHub
                  </a>
                </td>
                <td>
                  {imageUrls.length > 0 ? (
                    imageUrls.map((url, index) => (
                      <img
                        key={index}
                        src={url}
                        alt={`screenshot-${index}`}
                        width="60"
                        style={{ cursor: "pointer", marginRight: "5px", borderRadius: "4px" }}
                        onClick={() => window.open(url, "_blank")}
                      />
                    ))
                  ) : (
                    "No Image"
                  )}
                </td>
                <td>
                  {(!issue.status || issue.status === "unreviewed") ? (
                    <>
                      <button
                        onClick={() => handleMark(issue._id, "valid")}
                        style={{ marginRight: "8px", background: "green", color: "white" }}
                      >
                        Mark Valid
                      </button>
                      <button
                        onClick={() => handleMark(issue._id, "invalid")}
                        style={{ background: "red", color: "white" }}
                      >
                        Mark Invalid
                      </button>
                    </>
                  ) : (
                    <span style={{ fontWeight: "bold" }}>
                      ✅ {issue.status.toUpperCase()}
                    </span>
                  )}
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>

      {/* Modal */}
      {isModalOpen && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            backgroundColor: "rgba(0,0,0,0.5)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            zIndex: 1000,
          }}
          onClick={closeModal}
        >
          <div
            style={{
              backgroundColor: "white",
              padding: "20px",
              borderRadius: "8px",
              maxWidth: "600px",
              maxHeight: "80%",
              overflowY: "auto",
            }}
            onClick={(e) => e.stopPropagation()}
          >
            <h3>Issue Description</h3>
            <p style={{ whiteSpace: "pre-wrap" }}>{modalContent}</p>
            <button
              onClick={closeModal}
              style={{ marginTop: "10px", background: "#333", color: "white", padding: "5px 10px" }}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default IssueList;
