import React from "react";
import ReactMarkdown from "react-markdown";
import { updateIssueStatus } from "../services/issueService";

const IssueList = ({ issues, refresh }) => {
  if (!issues || issues.length === 0) return <p>No issues found</p>;

  const handleStatus = async (id, status) => {
    await updateIssueStatus(id, status);
    if (refresh) refresh();
  };

  const renderContent = (body) => {
    if (!body) return null;

    const words = body.split(/\s+/);
    return words.map((word, idx) => {
      if (/^https?:\/\/.*\.(jpg|jpeg|png|gif)$/i.test(word)) {
        return <img key={idx} src={word} alt="issue" style={{ maxWidth: "100%", marginTop: "5px", borderRadius: "5px" }} />;
      }
      return <ReactMarkdown key={idx}>{word}</ReactMarkdown>;
    });
  };

  return (
    <div style={{ marginTop: "20px" }}>
      <ul style={{ listStyle: "none", padding: 0 }}>
        {issues.map((issue) => (
          <li key={issue._id} style={{ border: "1px solid #ccc", padding: "10px", marginBottom: "10px", borderRadius: "5px" }}>
            <strong>{issue.title}</strong>
            <div style={{ marginTop: "10px" }}>{renderContent(issue.body)}</div>

            {issue.status === "unreviewed" && (
              <div style={{ marginTop: "10px" }}>
                <button onClick={() => handleStatus(issue._id, "valid")} style={{ marginRight: "10px" }}>✅ Valid</button>
                <button onClick={() => handleStatus(issue._id, "invalid")}>❌ Invalid</button>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default IssueList;
