import React, { useState } from "react";
import { updateIssueStatus } from "../services/issueService";
import "./IssueList.css";

// UI remains functionally unchanged, only design is improved

const IssueList = ({ issues, refresh }) => {
  const [modalContent, setModalContent] = useState("");
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Extract all images (Markdown and HTML <img>) for Screenshots column
  const extractImages = (body) => {
    if (!body) return [];
    const urls = [];

    // 1️⃣ Markdown images ![alt](url)
    const mdRegex = /!\[.*?\]\((.*?)\)/g;
    const mdMatches = [...body.matchAll(mdRegex)];
    mdMatches.forEach((m) => urls.push(m[1]));

    // 2️⃣ HTML <img src="...">
    const htmlRegex = /<img[^>]+src=["'](.*?)["']/g;
    const htmlMatches = [...body.matchAll(htmlRegex)];
    htmlMatches.forEach((m) => urls.push(m[1]));

    return urls;
  };

  // Extract text only for modal (remove Markdown and HTML images)
  const extractTextOnly = (body) => {
    if (!body) return "";
    let text = body.replace(/!\[.*?\]\(.*?\)/g, ""); // remove Markdown images
    text = text.replace(/<img[^>]+>/g, "");           // remove HTML <img>
    return text.trim();
  };

  const handleMark = async (id, status) => {
    await updateIssueStatus(id, status);
    refresh();
  };

  const openModal = (content) => {
    setModalContent(content);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setModalContent("");
  };

  return (
    <div className="il-container" style={{ border: "5px solid red" }}>
      <table className="il-table">
        <thead>
          <tr>
            <th>Title</th>
            <th>Repository</th>
            <th>Reporter</th>
            <th>Body</th>
            <th>Link</th>
            <th>Screenshots</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {issues.map((issue) => {
            const imageUrls = extractImages(issue.body);
            const textOnly = extractTextOnly(issue.body);

            return (
              <tr key={issue._id}>
                <td>{issue.title}</td>
                <td>{issue.repo}</td>
                <td>
                  <div className="il-avatar-reporter">
                    <span role="img" aria-label="reporter" className="il-avatar-icon">🧑‍💻</span>
                    <span>{issue.reporter}</span>
                  </div>
                </td>
                <td>
                  {textOnly ? (
                    <span
                      className="il-desc-link"
                      onClick={() => openModal(textOnly)}
                    >
                      View Description
                    </span>
                  ) : (
                    <span className="il-no-desc">—</span>
                  )}
                </td>
                <td>
                  <a
                    href={issue.url}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="il-gh-link"
                  >
                    View on GitHub
                  </a>
                </td>
                <td>
                  {imageUrls.length > 0 ? (
                    imageUrls.map((url, index) => (
                      <img
                        key={index}
                        src={url}
                        alt={`screenshot-${index}`}
                        width="60"
                        className="il-thumb"
                        onClick={() => window.open(url, "_blank")}
                      />
                    ))
                  ) : (
                    <span className="il-no-img">No Image</span>
                  )}
                </td>
                <td>
                  {(!issue.status || issue.status === "unreviewed") ? (
                    <>
                      <button
                        onClick={() => handleMark(issue._id, "valid")}
                        className="il-btn il-btn-valid"
                      >
                        Mark Valid
                      </button>
                      <button
                        onClick={() => handleMark(issue._id, "invalid")}
                        className="il-btn il-btn-invalid"
                      >
                        Mark Invalid
                      </button>
                    </>
                  ) : (
                    <span className="il-status">
                      ✅ {issue.status.toUpperCase()}
                    </span>
                  )}
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>

      {/* Modal */}
      {isModalOpen && (
        <div className="il-modal-backdrop" onClick={closeModal}>
          <div
            className="il-modal"
            onClick={(e) => e.stopPropagation()}
          >
            <h3 className="il-modal-title">Issue Description</h3>
            <p className="il-modal-content">{modalContent}</p>
            <button className="il-btn il-btn-close" onClick={closeModal}>
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default IssueList;
