import React from "react";
import { updateIssueStatus } from "../services/issueService";

const IssueList = ({ issues, onStatusChange }) => {
  if (!issues || issues.length === 0) return <p>No issues found</p>;

  const handleStatus = async (id, status) => {
    const updated = await updateIssueStatus(id, status);
    if (updated && onStatusChange) onStatusChange(); // refresh parent state
  };

  return (
    <div style={{ marginTop: "20px" }}>
      <h2>All Issues</h2>
      <ul style={{ listStyle: "none", padding: 0 }}>
        {issues.map((issue) => (
          <li
            key={issue._id}
            style={{
              border: "1px solid #ccc",
              padding: "10px",
              marginBottom: "10px",
              borderRadius: "5px",
            }}
          >
            <strong>{issue.title}</strong>
            <p>{issue.body}</p>
            <small>
              Repo: {issue.repo} | Status: {issue.status}
            </small>
            <div style={{ marginTop: "5px" }}>
              <button
                onClick={() => handleStatus(issue._id, "valid")}
                disabled={issue.status === "valid"}
                style={{ marginRight: "10px" }}
              >
                Valid
              </button>
              <button
                onClick={() => handleStatus(issue._id, "invalid")}
                disabled={issue.status === "invalid"}
              >
                Invalid
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default IssueList;
