import React from "react";
import "./IssueList.css";
import { updateIssueStatus } from "../services/issueService";

const IssueList = ({ issues, refresh }) => {
  // Function to extract first image URL from markdown body
  const extractImage = (body) => {
    if (!body) return null;
    const regex = /!\[.*?\]\((.*?)\)/;
    const match = body.match(regex);
    return match ? match[1] : null;
  };

  const handleMark = async (id, status) => {
    await updateIssueStatus(id, status);
    refresh(); // refresh list after marking
  };

  return (
    <div>
      <table>
        <thead>
          <tr>
            <th>Title</th>
            <th>Repository</th>
            <th>Reporter</th>
            <th>Link</th>
            <th>Screenshot</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {issues.map((issue) => {
            const imageUrl = extractImage(issue.body);

            return (
              <tr key={issue._id}>
                <td>{issue.title}</td>
                <td>{issue.repo}</td>
                <td>{issue.reporter}</td>
                <td>
                  <a href={issue.url} target="_blank" rel="noopener noreferrer">
                    View on GitHub
                  </a>
                </td>
                <td>
                  {imageUrl ? (
                    <img
                      src={imageUrl}
                      alt="screenshot"
                      width="60"
                      style={{ cursor: "pointer" }}
                      onClick={() => window.open(imageUrl, "_blank")}
                    />
                  ) : (
                    "No Image"
                  )}
                </td>
                <td>
                  {/* Only show buttons if issue is unreviewed */}
                  {(!issue.status || issue.status === "unreviewed") ? (
                    <>
                      <button
                        onClick={() => handleMark(issue._id, "valid")}
                        style={{ marginRight: "8px", background: "green", color: "white" }}
                      >
                        Mark Valid
                      </button>
                      <button
                        onClick={() => handleMark(issue._id, "invalid")}
                        style={{ background: "red", color: "white" }}
                      >
                        Mark Invalid
                      </button>
                    </>
                  ) : (
                    <span style={{ fontWeight: "bold" }}>
                      âœ… {issue.status.toUpperCase()}
                    </span>
                  )}
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default IssueList;
