import React, { useState } from "react";

const IssueList = ({ issues }) => {
  if (!issues || issues.length === 0) return <p>No issues found</p>;

  // For image preview popup
  const [previewImg, setPreviewImg] = useState(null);

  return (
    <div style={{ marginTop: "20px" }}>
      <table
        style={{
          width: "100%",
          borderCollapse: "collapse",
          marginBottom: "20px"
        }}
      >
        <thead>
          <tr style={{ background: "#f2f2f2" }}>
            <th style={styles.th}>Title</th>
            <th style={styles.th}>Repository</th>
            <th style={styles.th}>Reporter</th>
            <th style={styles.th}>Details</th>
            <th style={styles.th}>Images</th>
          </tr>
        </thead>
        <tbody>
          {issues.map((issue) => (
            <tr key={issue._id} style={{ borderBottom: "1px solid #ccc" }}>
              <td style={styles.td}>{issue.title}</td>
              <td style={styles.td}>{issue.repo || "N/A"}</td>
              <td style={styles.td}>{issue.reporter || "Unknown"}</td>
              <td style={styles.td}>
                {issue.url ? (
                  <a
                    href={issue.url}
                    target="_blank"
                    rel="noopener noreferrer"
                    style={{ color: "#007bff" }}
                  >
                    View Issue
                  </a>
                ) : (
                  "N/A"
                )}
              </td>
              <td style={styles.td}>
                {issue.images && issue.images.length > 0 ? (
                  issue.images.map((img, idx) => (
                    <img
                      key={idx}
                      src={img}
                      alt="issue"
                      style={{
                        width: "50px",
                        height: "50px",
                        objectFit: "cover",
                        borderRadius: "5px",
                        marginRight: "5px",
                        cursor: "pointer"
                      }}
                      onClick={() => setPreviewImg(img)}
                    />
                  ))
                ) : (
                  "No image"
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Image Preview Modal */}
      {previewImg && (
        <div
          onClick={() => setPreviewImg(null)}
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: "rgba(0,0,0,0.7)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            zIndex: 1000
          }}
        >
          <img
            src={previewImg}
            alt="Preview"
            style={{ maxWidth: "80%", maxHeight: "80%" }}
          />
        </div>
      )}
    </div>
  );
};

const styles = {
  th: {
    padding: "10px",
    border: "1px solid #ddd",
    textAlign: "left"
  },
  td: {
    padding: "10px",
    border: "1px solid #ddd",
    verticalAlign: "top"
  }
};

export default IssueList;
