import React, { useState } from "react";
import "./IssueList.css";


const IssueList = ({ issues }) => {
  const [selectedImage, setSelectedImage] = useState(null);

  if (!issues || issues.length === 0) {
    return <p>No issues found.</p>;
  }

  return (
    <div>
      <table className="issue-table">
        <thead>
          <tr>
            <th>Title</th>
            <th>Repository</th>
            <th>Reporter</th>
            <th>Link</th>
            <th>Screenshot</th>
          </tr>
        </thead>
        <tbody>
          {issues.map((issue) => (
            <tr key={issue._id}>
              <td>{issue.title}</td>
              <td>{issue.repository}</td>
              <td>{issue.reporter}</td>
              <td>
                <a
                  href={issue.html_url}
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  View on GitHub
                </a>
              </td>
              <td>
                {issue.screenshot && (
                  <img
                    src={issue.screenshot}
                    alt="screenshot"
                    style={{ width: "80px", cursor: "pointer" }}
                    onClick={() => setSelectedImage(issue.screenshot)}
                  />
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Modal for full image */}
      {selectedImage && (
        <div
          className="modal"
          onClick={() => setSelectedImage(null)}
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            backgroundColor: "rgba(0,0,0,0.8)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <img
            src={selectedImage}
            alt="Full"
            style={{
              maxWidth: "90%",
              maxHeight: "90%",
              borderRadius: "10px",
            }}
          />
        </div>
      )}
    </div>
  );
};

export default IssueList;
